import React from 'react'
import Script from "react-inline-script"


class Piio extends React.Component {
  constructor(props) {
    super(props)

    //Append preconnect and preload to head
    var preconnect = document.createElement('link');
    preconnect.rel = 'preconnect';
    preconnect.href = '//pcdn.piiojs.com';
    document.head.appendChild(preconnect);

    var preload = document.createElement('link');
    preload.rel = 'preload';
    preload.as = 'script';
    preload.href = '//pcdn.piiojs.com/'+this.props.domainKey+'/image.min.js';
    document.head.appendChild(preload);
  }

  render() {
    return (
      <div>
        <Script>
        {`
        (function(i,m,a,g,e) {
e = i.getElementsByTagName(m)[0], (g = i.createElement(m)).src = "//pcdn.piiojs.com/"+a+"/image.min.js",
g.onerror = function() {
    (g = i.createElement(m)).src = "https://fs.piio.co/image-failover.min.js",
    e.parentNode.insertBefore(g, e);
}, e.parentNode.insertBefore(g, e)
}(document, "script", "{this.props.domainKey}"));
  `}
  </Script>
      </div>)
    }
}
export default Piio;
