import React from 'react'
import styles from './styles.module.css'

function selectTag(tagName) {
  if ((tagName === 'img') || (tagName === 'source')) {
    return <data-piio className={styles.source}></data-piio>
  } else {
    return <data-piio-bck className={styles.background}></data-piio-bck>
  }
}
export const Piio = ({ tag }) => {
  return selectTag(tag)
}
